import { FETCH_POSTS, FETCH_LATEST } from "../actions/types";

export const fetchPosts = () => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_POSTS,
				payload: data,
			});
		})
		.catch((err) => console.log(err));
};

export const fetchLatest = () => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_LATEST,
				payload: data[0],
			});
		})
		.catch((err) => console.log(err));
};

export const fetchArticle = (heading) => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {

			dispatch({
				type: FETCH_LATEST,
				payload: data,
			});
		})
		.catch((err) => console.log(err));
};
