// function fetchPost() {}

let initialPosts;

function getUsers() {
	try {
		axios.get("/blog/articles.json").then((response) => {
			initialPosts = response.data;
		});
	} catch (error) {
		console.log("An error occured while trying to fetch the data");
	}
}

