import React from "react";
import {
	FacebookShareButton,
	TwitterShareButton,
	FacebookIcon,
	LinkedinShareButton,
	LinkedinIcon,
	WhatsappIcon,
	TwitterIcon,
} from "react-share";

const ShareArticle = ({ tag, title, img, intro }) => {
	const param = {
		url: String(window.location), // to share current page
		title: "Steadylearner Website",
		shareImage: { img },
		size: "14px",
	};

	return (
		<div className="mt-5 d-flex flex-column justify-content-center align-items-center share">
			<h4 className="mb-3">Share this post</h4>
			<div>
				<FacebookShareButton url={param.url} quote={title} hashtag={tag} className="mx-2">
					<FacebookIcon size={param.size} />
				</FacebookShareButton>
				<TwitterShareButton title={title} hashtags={tag}>
					<TwitterIcon size={param.size} />
				</TwitterShareButton>
				<LinkedinShareButton title={title} summary={intro} source={param.url}>
					<btn href="javascript;;" className="d-inline-block rounded-circle bg-primary text-center mx-2 btn-outline-secondary">
						<i className="fa fa-linkedin text-light"></i>
					</btn>
				</LinkedinShareButton>

				<a href="javascript;;" className="d-inline-block rounded-circle bg-primary text-center mx-2 btn-outline-secondary">
					<i className="fa fa-instagram text-light"></i>
				</a>
			</div>
		</div>
	);
};

export default ShareArticle;
