import { FETCH_POSTS, FETCH_LATEST } from "../actions/types";

export const fetchPosts = () => (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_POSTS,
				payload: data,
			});
		})
		.catch((err) => console.log(err));
};
