export const fetchLatest = () => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_LATEST,
				payload: data[0],
			});
		})
		.catch((err) => console.log(err));
};

export const fetchArticle = (param) => async (dispatch) => {
	console.log("Working");
	const fetchData = fetch("/blog/articles.json")
		const json = await fetchData.then((res) => res.json())
		.then((data) => data.find((item) => item.heading === param))
		.then((article) => {
			console.log(article);
			dispatch({
				type: FETCH_ARTICLE,
				payload: article,
			});
		})
		.catch((err) => console.log(err));
};
