import axios from "axios";

// function fetchPost() {}

let initialPosts = function getUsers() {
	async () => {
		try {
			const request = await axios({
				method: "get",
				url: "/blog/articles.json",
            })
            const data = await request.json();
            return data;
        } catch (error) {
            console.log("Unable to fetch blog posts");
        }
	};
};
