import React, { lazy, Suspense, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { HashLoader } from "react-spinners";

// import { createStore } from "redux";

const Footer = lazy(() => import("../Reusable Components/Footer"));
const NavBar = lazy(() => import("../Reusable Components/Navbar"));
const Banner = lazy(() => import("./Banner"));
const BlogSection = lazy(() => import("./BlogSection"));
const Subscribe = lazy(() => import("../Reusable Components/Subscribe"));
const ScrollToTop = lazy(() => import("../Reusable Components/ScrollToTop"));

const Blog = () => {
	const { pathname } = useLocation();
	const [ APIResponse, setAPIResponse ] = useState("");

	function callAPI() {
		fetch("http://localhost:9000/API")
			.then((res) => res.json())
			.then((res) => {
				setAPIResponse(res);
			});
	};

	callAPI();
	console.log()

	useEffect(() => {
		window.scrollTo(0, 0);
	}, [pathname]);

	return (
		<div className="blog">
			<Suspense
				fallback={
					<div className="fallback">
						<HashLoader color={"#1c3ae3"} loading />
					</div>
				}
			>
				{/* {APIResponse} */}
				<NavBar />
				<Banner />
				<BlogSection />
				<Subscribe />
				<Footer />
				<ScrollToTop />
			</Suspense>
		</div>
	);
};

export default Blog;
