export default function componentLoader(lazyComponent){var attemptsLeft=arguments.length>1&&arguments[1]!==undefined?arguments[1]:3;return new Promise(function(resolve,reject){lazyComponent().then(resolve).catch(function(error){// retry after 1500ms
setTimeout(function(){if(attemptsLeft===1){reject(error);return;}// Call componentLoader again
componentLoader(lazyComponent,attemptsLeft-1).then(resolve,reject);},1500);});});}
