import { FETCH_POSTS, FETCH_LATEST } from "../actions/types";

const initialState = {
	allPosts: [],
	post: {},
};

export default function blogPostsReducer (state = initialState, action) {
	switch (action.type) {
		case FETCH_POSTS:
			console.log("reducer");
			return {
				...state,
				allPosts: action.payload,
			};
		case FETCH_POSTS:
			console.log("reducer");
			return {
				...state,
				allPosts: action.payload,
			};
		default:
			return state;
	}
};
