import React from "react";

const Subscribe = () => {
	return (
		<div className="container-fluid bg-primary py-5">
			<div className="container text-center">
                <h3 className="text-light font-italic font-weight-bold mb-3">Subscribe to our newsletters</h3>
				<form className="" action="">
					<div className="form-group mb-2 d-flex flex-column flex-lg-row align-items-center">
						<input className="p-2 px-md-4 form-control w-100 border-0" type="email" placeholder="yourname@email.com" required />
						<button className="btn bg-primary__light text-light p-2 w-100 w-lg-a" type="submit">
							Submit
						</button>
					</div>
				</form>
			</div>
		</div>
	);
};

export default Subscribe;
