// 	})
// 	.catch((error) => {
// 		console.log("Unable to fetch blog posts");
// 	});

console.log(initialPosts);

export const blogPosts = (state = initialPosts, action) => {
	switch (action.type) {
		case "GET_ALL_POSTS":
			return state;
		case "GET_LATEST_POSTS":
			return state.slice(action.number);
		default:
			return state;
	}
};
