import React from "react";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Slides = () => {
	let settings = {
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoPlay: true,
		autoPlaySpeed: 100,
		cssEase: "linear",
	};

	const data = [
		{
			image: "/images/lmj-at-office.jpg",
			content: {
				text1: "Welcome to the official wesite of",
				text2: "Hon Olamijuwon Alao-Akala"
			}
		},
		{
			image: "/images/lmj-at-office.jpg",
			content: {
				text1: "Welcome to the official wesite of",
				text2: "Hon Olamijuwon Alao-Akala"
			}
		}
	]

	return (
		<div className="container-fluid px-0 mx-0 slides">
			<Slider {...settings}>
				{data.map((d, i) => {
					return (
						<div className="main">

						</div>
					)
				})}


				<div className="main" style={{ background: "red", fontSize: "234px" }}>
					{/* <img src="/images/lmj-at-office.jpg" alt="Lamiju Alao-Akala" /> */}
					<div className="overlay d-flex justify-content-center align-items-center">
						<div className="p-3 p-md-4 p-lg-5 ml-auto bg-secondary d-flex flex-column justify-content-center align-items-center rounded-circle shadow mr-md-5">
							<p className="text-center">
								Welcome to the official website of
								<br />
								<span className="font-weight-bold">Hon. Olamijuwon Alao-Akala</span>
							</p>
						</div>
					</div>
				</div>
				<div>
					<img src="/images/lmj001.jpg" alt="Lamiju Alao-Akala" />
				</div>
			</Slider>
		</div>
	);
};

export default Slides;
