import React from "react";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Slides = () => {
	let settings = {
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoplay: true,
		autoplaySpeed: 1000,
		cssEase: "linear",
	};

	return (
		<div className="container-fluid px-0 mx-0 slides">
			<Slider {...settings}>
				<div className="slide">
					<img src="/images/lmj-at-office.jpg" alt="" />
					<div className="overlay bg-secondary rounded-top text-center p-2 px-md-5 py-md-3 d-flex flex-column justify-content-center align-items-center">
						<>
							Welcome to the official website of <span className="font-weight-bold">Hon Olamijuwonlo Alao-Akala</span>
						</>
					</div>
				</div>
				<div className="slide">
					<img src="/images/lmj001.jpg" alt="" />
					<div className="overlay bg-secondary rounded-top text-center p-2 px-md-5 py-md-3 d-flex flex-column justify-content-center align-items-center">
						<>
							Welcome to the official website of <span className="font-weight-bold">Hon Olamijuwonlo Alao-Akala</span>
						</>
					</div>
				</div>
			</Slider>
		</div>
	);
};

export default Slides;
