import React from "react";
import { Link } from "react-router-dom";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const BlogLatest = () => {
	let settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1
	};

	return (
		<div className="container-fluid px-0">
			<div className="container my-5 blog-latest">
				<h3 className="font-weight-bold">Latest blog post</h3>
				<div className="row">
					<div className="col-lg-6 mb-3 mb-lg-5">
						<img className="w-100" src="/images/lmj02.jpg" alt="LMJ sitting with an elderly woman" />
					</div>
					<div className="col-lg-6">
						<h2>Reaching out to the underprivileged</h2>
						<p>
							Lorem ipsum dolor sit amet consectetur, adipisicing elit. Quaerat, amet saepe corrupti esse mollitia minus
							voluptate! Nulla reiciendis placeat, iusto in explicabo at quisquam tenetur, nemo magni laboriosam sed. Eum, vel
							voluptatum. Eligendi temporibus aspernatur assumenda? Eius maxime facilis quod?...
						</p>
						<Link to="/">
							<button className="btn btn-outline-primary">Read more</button>
						</Link>
					</div>
				</div>
			</div>

			<div className="blog-news" style={{ background: `url('/images/lmj-sit.jpg') no-repeat` }}>
				<div className="overlay w-100 py-5">
					<div className="container d-flex flex-column h-100 justify-content-center align-items-start">
						<h2 className="text-light font-weight-bold">Blog News</h2>
						<div className="d-flex justify-content-center align-items-center w-100">
							<Slider {...settings}>
								<div className="my-3" style={{width: "33%"}}>
									<div class="card shadow">
										<img class="card-img-top" src="/images/lmj-portrait.jpg" alt="LMJ Portrait" />
										<div class="card-body">
											<h4 class="card-title font-weight-bold">Politics</h4>
											<p class="card-text">
												Lorem ipsum dolor sit amet consectetur adipisicing elit. Fugiat fugit voluptatum, unde
												nesciunt enim sed asperiores nam, impedit non, nobis blanditiis corporis sequi odio? Nobis
												autem quos voluptatibus in quia.
											</p>
											<Link className="bg-primary__light text-secondary__light btn more">Read more</Link>
										</div>
									</div>
								</div>
							</Slider>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};

export default BlogLatest;

