import React from "react";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Slides = () => {
	let settings = {
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoPlay: true,
		autoPlaySpeed: 100,
		cssEase: "linear",
	};



	return (
		<div className="container-fluid px-0 mx-0 slides">
			<Slider {...settings}>
				<div className="slide">
					<img src="/images/lmj-at-office.jpg" alt=""/>
					<div className="overlay bg-secondary__light p-2 p-lg- rounded-circle d-flex flex-column justify-content-center align-items-center">
						<>Welcome to the official website of <span>Hon Olamijuwonlo Alao-Akala</span></>
					</div>

				</div>

			</Slider>
		</div>
	);
};

export default Slides;
