import { FETCH_POSTS, FETCH_LATEST } from "../actions/types";

export const fetchPosts = () => (dispatch) => {
	console.log("actions");
	return async (dispatch) => {
		const response = await fetch("/blog/articles.json")
			.then((res) => res.json())
			.then((data) => {
				dispatch({
					type: FETCH_POSTS,
					payload: data,
				});
				console.log(data);
			})
			.catch((err) => console.log(err));

	};

	// fetch("/blog/articles.json")
