	// 		payload: data,
	// 	});
	// 	console.log(data);
	// })
	// .catch((err) => console.log(err));

	const loadPosts = async () => {
		console.log("Just fetching");
		setIsLoading(true);
		await dispatch(fetchPosts());
		setIsLoading(false);
		console.log("After fetching");
	};
};
