import React from "react";
import { FacebookShareButton, TwitterShareButton, FacebookIcon, LinkedinIcon, WhatsappIcon, TwitterIcon } from "react-share";

const ShareArticle = ({ tag, title, img }) => {
	const param = {
		url: String(window.location), // to share current page
		title: "Steadylearner Website",
		shareImage: "https://www.steadylearner.com/static/images/brand/prop-passer.png",
		size: "14px",
	};

	return (
		<div className="mt-5 d-flex flex-column justify-content-center align-items-center share">
			<h4 className="mb-3">Share this post</h4>
			<div>
				<FacebookShareButton
					url={param.url}
					quote={title}
					className="rounded-circle bg-primary text-center mx-2 btn-outline-secondary"
				>
					{/* <a href="javascript;;" className="d-inline-block rounded-circle bg-primary text-center mx-2 btn-outline-secondary"> */}
					<FacebookIcon size={param.size} />
					{/* </a> */}
				</FacebookShareButton>
				<a href="javascript;;" className="d-inline-block rounded-circle bg-primary text-center mx-2 btn-outline-secondary">
					<i className="fa fa-twitter text-light"></i>
				</a>
				<a href="javascript;;" className="d-inline-block rounded-circle bg-primary text-center mx-2 btn-outline-secondary">
					<i className="fa fa-linkedin text-light"></i>
				</a>
				<a href="javascript;;" className="d-inline-block rounded-circle bg-primary text-center mx-2 btn-outline-secondary">
					<i className="fa fa-instagram text-light"></i>
				</a>
			</div>
		</div>
	);
};

export default ShareArticle;
