	// 	.then((data) => {
	// 		dispatch({
	// 			type: FETCH_POSTS,
	// 			payload: data,
	// 		});
	// 	})
	// 	.catch((err) => console.log(err));
	axios
		.get("/api")
		.then((res) => res.data)
		.then((data) => {
			dispatch({
				type: FETCH_POSTS,
				payload: data,
			});
		})
		.catch((err) => console.log(err));
};

