};

export const fetchArticle = (param) => async (dispatch) => {
	const fetchData = await fetch("/api");
	const json = await fetchData.json();
	const data = await json.find((item) => item.heading === param);
	await dispatch({
		type: FETCH_ARTICLE,
		payload: data,
