import React from "react";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Slides = () => {
	let settings = {
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
	};
	return (
		<div className="container-fluid px-0 mx-0 slides">
			<Slider {...settings}>
				<div className="main">
					<img src="/images/lmj-at-office.jpg" alt="Lamiju Alao-Akala" />
					<div className="overlay d-flex justify-content-center align-items-center">
						<div className="p-3 ml-auto bg-secondary d-flex flex-column justify-content-center align-items-center rounded-circle shadow mr-">
							<h3>Title</h3>
							<p>
								Lorem ipsum dolor sit amet consectetur, adipisicing elit. Nam totam rem tempora odio possimus quo aliquid
								corporis, minus debitis dolor. Quasi non ab, vitae quae debitis quam animi dolorum error!
							</p>
						</div>
					</div>
				</div>
				<div>
					<img src="/images/lmj001.jpg" alt="Lamiju Alao-Akala" />
				</div>
			</Slider>
		</div>
	);
};

export default Slides;
