import { FETCH_POSTS, FETCH_LATEST } from "../actions/types";

const initialState = {
	posts: [],
	post: {},
};

export default blogPostsReducer ((state = initialState, action) => {
	switch (action.type) {
		case FETCH_POSTS:
			console.log("reducer");
			return {
				...state,
				posts: action.payload,
			};
		default:
			return state;
	}
})
