import { FETCH_POSTS, FETCH_LATEST } from "../actions/types";

export const fetchPosts = () => async (dispatch) => {
	console.log("before fetching from actions");
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_POSTS,
				payload: data,
			});
			console.log(data);
		})
		.catch((err) => console.log(err));

	// const response = await fetch("/blog/articles.json");
