import { FETCH_POSTS, FETCH_LATEST, FETCH_ARTICLE } from "../actions/types";

export const fetchPosts = () => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_POSTS,
				payload: data,
			});
		})
		.catch((err) => console.log(err));
};

export const fetchLatest = () => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			dispatch({
				type: FETCH_LATEST,
				payload: data[0],
			});
		})
		.catch((err) => console.log(err));
};

export const fetchArticle = () => async (dispatch) => {
	fetch("/blog/articles.json")
		.then((res) => res.json())
		.then((data) => {
			console.log(param);
			data.find((item) => item.heading === param).then((item) => {
				console.log(item);
				dispatch({
					type: FETCH_ARTICLE,
					payload: item,
				});
			});
		})
		.catch((err) => console.log(err));
};
