import axios from "axios";

function fetchPost() {
    return axios.get("../../backend/blog-posts.json")
    .then(response => response.data);
}

const initialPosts = fetchPost();

export const blogPosts = (state, action) => {
    switch(action.type) {
        case "GET_ALL_POSTS":
            return state;
        case "GET_LATEST_POSTS":
            return state.slice(action.number);
        default:
            return state;
    }
}
